n0:createOracleTrigger.n0:source:
  CREATE OR REPLACE EDITIONABLE TRIGGER "PHONEBOOK"."BI_PHONEBOOK" 
  before insert on "PHONEBOOK"               
  for each row  
begin   
  if :NEW."ID" is null then 
    select "PHONEBOOK_SEQ".nextval into :NEW."ID" from sys.dual; 
  end if; 
end;

/
ALTER TRIGGER "PHONEBOOK"."BI_PHONEBOOK" ENABLE
